contracts/AMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { Projects } from './Projects.sol';

import { IERC20 } from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import { SafeERC20 } from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import { IWETH } from './interfaces/IWETH.sol';
import { IProjectTokenFactory } from './tokens/interfaces/IProjectTokenFactory.sol';
import { IPoolFactory } from './pools/interfaces/IPoolFactory.sol';
import { IProjectTokenERC20 } from './tokens/interfaces/IProjectTokenERC20.sol';
import './common/Constants.sol';

import 'hardhat/console.sol';

contract AMM is Projects {
	address internal fansSociety;
	address internal weth;

	address internal tokenFactory;
	address internal poolFactory;

	event TokensClaimed(
		uint256 indexed projectId,
		address indexed token,
		address indexed caller,
		uint256 amount
	);

	constructor(
		address _fansSocietyAddress,
		address _wethTokenAddress,
		address _tokenFactoryAddress,
		address _poolFactoryAddress
	) Projects() {
		fansSociety = _fansSocietyAddress;
		weth = _wethTokenAddress;
		tokenFactory = _tokenFactoryAddress;
		poolFactory = _poolFactoryAddress;
	}

	function launchProject(
		uint256 _id
	) external statusIs(_id, ProjectStatus.Completed) {
		Project memory project = projects[_id];

		uint40 totalTokenSupply = uint40(MULTIPLIER) * project.totalSupply;

		uint40 ammTokenSupplyShares = uint40(AMM_SUPPLY) * totalTokenSupply;

		uint40 authorTokenSupplyShares = uint40(AUTHOR_SUPPLY) * totalTokenSupply;

		uint40 ammTokensPoolShares = (uint40(AMM_TOKENS_POOL_SHARES) *
			ammTokenSupplyShares) / 100;

		projects[_id].tokenAddress = IProjectTokenFactory(tokenFactory).createToken(
			project.name,
			project.symbol,
			address(this),
			uint40(MULTIPLIER) * totalTokenSupply,
			ammTokenSupplyShares + authorTokenSupplyShares
		);

		address pool = IPoolFactory(poolFactory).createPool(
			projects[_id].tokenAddress,
			weth
		);

		// ===== AMM transfer tokens to author =====
		IProjectTokenERC20(projects[_id].tokenAddress).safeTransferFrom(
			address(this),
			projects[_id].authorAddress,
			authorTokenSupplyShares
		);
		// ===== AMM transfer tokens to pool =====
		IProjectTokenERC20(projects[_id].tokenAddress).safeTransferFrom(
			address(this),
			pool,
			ammTokensPoolShares
		);

		//	// swap with weth
		//	IWETH(weth).deposit{ value: project.fund }();
		//	// transfer weth
		//	assert(IWETH(weth).transfer(pool, project.fund));

		projects[_id].status = ProjectStatus.Launched;
		emit ProjectStatusChanged(_id, ProjectStatus.Launched);
	}

	function claimProjectTokens(
		uint256 _projectId
	)
		external
		statusIs(_projectId, ProjectStatus.Launched)
		isCommited(_projectId)
	{
		Project memory project = projects[_projectId];

		uint256 commitment = commitments[_projectId][msg.sender];

		uint256 tokenAmount = (INVESTORS_SUPPLY * project.totalSupply * commitment) /
			project.fund;

		IProjectTokenERC20(project.tokenAddress).claim(msg.sender, tokenAmount);

		emit TokensClaimed(_projectId, project.tokenAddress, msg.sender, tokenAmount);
	}
}

contracts/Projects.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import { Initializable } from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import { IERC20 } from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import { ERC20Wrapper } from '@openzeppelin/contracts/token/ERC20/extensions/ERC20Wrapper.sol';
import { Ownable } from '@openzeppelin/contracts/access/Ownable.sol';

import './common/Constants.sol';

contract Projects is Ownable {
	enum ProjectStatus {
		Opened,
		Aborted,
		Completed,
		Launched
	}

	struct Project {
		string name;
		string symbol;
		string description;
		uint id;
		uint fund;
		uint target;
		uint minInvest;
		uint maxInvest;
		uint32 totalSupply;
		ProjectStatus status;
		address authorAddress;
		address tokenAddress;
	}

	uint public count;

	mapping(uint => Project) public projects;

	mapping(uint => mapping(address => uint)) public commitments;

	event ProjectCreated(
		uint id,
		string name,
		string symbol,
		string description,
		uint target,
		uint minInvest,
		uint maxInvest,
		uint32 totalSupply,
		address indexed authorAddress
	);

	event Committed(uint indexed id, address indexed caller, uint amount);

	event Withdrawed(uint indexed id, address indexed caller, uint amount);

	event ProjectStatusChanged(uint indexed id, ProjectStatus status);

	modifier onlyAuthor(uint _id) {
		require(msg.sender == projects[_id].authorAddress, 'Not author');
		_;
	}

	modifier statusIs(uint _id, ProjectStatus _status) {
		require(projects[_id].status == _status, 'Bad project status');
		_;
	}

	modifier statusLessThan(uint _id, ProjectStatus _status) {
		require(projects[_id].status < _status, 'Bad project status');
		_;
	}

	modifier isCommited(uint _id) {
		require(commitments[_id][msg.sender] > 0, 'No commitment');
		_;
	}

	function createProject(
		address _authorAddress,
		string calldata _name,
		string calldata _symbol,
		string calldata _description,
		uint _target,
		uint _minInvest,
		uint _maxInvest,
		uint32 _totalSupply
	) external onlyOwner {
		count++;

		projects[count] = Project({
			id: count,
			name: _name,
			symbol: _symbol,
			description: _description,
			fund: 0,
			target: _target,
			minInvest: _minInvest,
			maxInvest: _maxInvest,
			totalSupply: _totalSupply,
			status: ProjectStatus.Opened,
			authorAddress: _authorAddress,
			tokenAddress: address(0)
		});

		emit ProjectCreated(
			count,
			_name,
			_symbol,
			_description,
			_target,
			_minInvest,
			_maxInvest,
			_totalSupply,
			_authorAddress
		);
	}

	function abortProject(
		uint _id
	) external onlyOwner statusIs(_id, ProjectStatus.Opened) {
		projects[_id].status = ProjectStatus.Aborted;
		emit ProjectStatusChanged(_id, ProjectStatus.Aborted);
	}

	function commitOnProject(
		uint _id
	) external payable statusIs(_id, ProjectStatus.Opened) {
		Project storage project = projects[_id];
		require(
			msg.value + commitments[_id][msg.sender] >= project.minInvest,
			'Not enough'
		);
		require(
			msg.value + commitments[_id][msg.sender] <= project.maxInvest,
			'Too much'
		);

		project.fund += msg.value;

		commitments[_id][msg.sender] += msg.value;
		emit Committed(_id, msg.sender, msg.value);

		if (project.fund >= project.target) {
			project.status = ProjectStatus.Completed;
			emit ProjectStatusChanged(_id, ProjectStatus.Completed);
		}
	}

	function withdrawOnProject(
		uint _id
	) external statusLessThan(_id, ProjectStatus.Completed) isCommited(_id) {
		uint commitment = commitments[_id][msg.sender];

		projects[_id].fund -= commitment;

		commitments[_id][msg.sender] = 0;

		(bool sent, ) = msg.sender.call{ value: commitment }('');
		require(sent, 'withdraw failed');

		emit Withdrawed(_id, msg.sender, commitment);
	}
}

