// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { Clones } from '@openzeppelin/contracts/proxy/Clones.sol';

import { IProjectTokenFactory } from './interfaces/IProjectTokenFactory.sol';
import { ProjectTokenERC20 } from './ProjectTokenERC20.sol';

contract ProjectTokenFactory is IProjectTokenFactory {
	address private immutable implementation;

	event TokenCreated(address token, string name, string symbol);

	constructor() {
		implementation = address(new ProjectTokenERC20());
	}

	function createToken(
		string memory _name,
		string memory _symbol,
		address _amm,
		uint40 _totalSupply,
		uint40 _initialSupply
	) public returns (address) {
		address token = Clones.clone(implementation);
		ProjectTokenERC20(token).initialize(
			_name,
			_symbol,
			_amm,
			_totalSupply,
			_initialSupply
		);
		emit TokenCreated(token, _name, _symbol);
		return token;
	}
}

contracts/tokens/ProjectTokenERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { Initializable } from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import { ERC20Upgradeable } from '@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol';
import { SafeERC20Upgradeable } from '@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol';

import { IProjectTokenERC20 } from './interfaces/IProjectTokenERC20.sol';
import 'hardhat/console.sol';

contract ProjectTokenERC20 is
	Initializable,
	ERC20Upgradeable,
	IProjectTokenERC20
{
	uint256 public maxTotalSupply;

	address private amm;

	mapping(address => bool) private claimed;

	event Claimed(address account, uint amount);

	modifier onlyAmm() {
		require(msg.sender == amm, 'Caller is not AMM');
		_;
	}

	function initialize(
		string memory _name,
		string memory _symbol,
		address _amm,
		uint40 _totalSupply,
		uint40 _initialSupply
	) public virtual initializer {
		require(_totalSupply >= _initialSupply, 'total supply too small');
		amm = _amm;
		maxTotalSupply = _totalSupply;

		__ProjectTokenERC20_init(_name, _symbol, _initialSupply);
	}

	/**
	 * @dev Mint supplies
	 * - `initialSupply` amount of tokens to mint and assign to `amm` contract.
	 *
	 * See {ERC20-constructor}.
	 */
	function __ProjectTokenERC20_init(
		string memory _name,
		string memory _symbol,
		uint40 _initialSupply
	) internal onlyInitializing {
		__ERC20_init_unchained(_name, _symbol);
		__ProjectTokenERC20_init_unchained(_initialSupply);
	}

	function __ProjectTokenERC20_init_unchained(
		uint40 _initialSupply
	) internal onlyInitializing {
		_mint(amm, _initialSupply);
	}

	function safeTransferFrom(
		address from,
		address to,
		uint256 value
	) external onlyAmm {
		_approve(from, amm, value);
		_transfer(from, to, value);
	}

	function claim(address account, uint256 amount) external onlyAmm {
		require(!claimed[account], 'already claimed');
		uint256 totalSupply = totalSupply();
		require((totalSupply + amount) <= maxTotalSupply, 'maxTotalSupply limit');
		_mint(account, amount);
		claimed[account] = true;
		emit Claimed(account, amount);
	}
}

